import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Vocab.css';

type VocabWord = {
  word: string;
  meaning: string;
  example?: string;
  known?: boolean;
};

const LS_KEY = 'vocab_words';

export default function Vocab() {
  const navigate = useNavigate();

  const [words, setWords] = useState<VocabWord[]>([]);
  const [newWord, setNewWord] = useState('');
  const [newMeaning, setNewMeaning] = useState('');
  const [newExample, setNewExample] = useState('');
  const [error, setError] = useState<string | null>(null);

  // 1) 로컬스토리지 불러오기
  useEffect(() => {
    const saved = localStorage.getItem(LS_KEY);
    if (saved) {
      setWords(JSON.parse(saved));
    } else {
      // 최초 한 번만 기본 예시 넣고 저장
      const seed: VocabWord[] = [
        { word: 'improve', meaning: 'to make better', example: 'I want to improve my English.' },
        { word: 'conversation', meaning: 'talking with someone', example: 'We had a nice conversation at lunch.' },
      ];
      setWords(seed);
      localStorage.setItem(LS_KEY, JSON.stringify(seed));
    }
  }, []);

  // 2) 변경 시 저장
  useEffect(() => {
    localStorage.setItem(LS_KEY, JSON.stringify(words));
  }, [words]);

  // 체크 토글
  const toggleKnown = (index: number) => {
    setWords(prev => {
      const next = [...prev];
      next[index].known = !next[index].known;
      return next;
    });
  };

  // 단어 추가
  const addWord = () => {
    const w = newWord.trim();
    const m = newMeaning.trim();
    const e = newExample.trim();

    if (!w || !m) {
      setError('단어와 뜻을 입력해 주세요.');
      return;
    }

    const exists = words.some(item => item.word.trim().toLowerCase() === w.toLowerCase());
    if (exists) {
      setError('이미 추가된 단어예요.');
      return;
    }

    setWords(prev => [{ word: w, meaning: m, example: e }, ...prev]);
    setNewWord('');
    setNewMeaning('');
    setNewExample('');
    setError(null);
  };

  // 엔터키로 추가
  const onKeyDown = (e: React.KeyboardEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      addWord();
    }
  };

  // 삭제
  const removeWord = (index: number) => {
    setWords(prev => prev.filter((_, i) => i !== index));
  };

  return (
    <div className="vocab-container">
      <div className="vocab-box">
        <div className="vocab-header">
          <button className="back-button" onClick={() => navigate('/home')}>
            &lt;
          </button>
          <h2>📚 단어장</h2>
        </div>

        {/* 입력 폼 */}
        <div className="vocab-form">
          <input
            className="vocab-input"
            placeholder="단어 (예: appreciate)"
            value={newWord}
            onChange={(e) => setNewWord(e.target.value)}
            onKeyDown={onKeyDown}
          />
          <input
            className="vocab-input"
            placeholder="뜻 (예: 고맙게 여기다)"
            value={newMeaning}
            onChange={(e) => setNewMeaning(e.target.value)}
            onKeyDown={onKeyDown}
          />
          <input
            className="vocab-input"
            placeholder="예문 (선택)"
            value={newExample}
            onChange={(e) => setNewExample(e.target.value)}
            onKeyDown={onKeyDown}
          />
          <button className="add-btn" onClick={addWord}>추가</button>
        </div>
        {error && <p className="form-error">{error}</p>}

        {words.length === 0 ? (
          <p>저장된 단어가 없습니다.</p>
        ) : (
          <ul className="vocab-list">
            {words.map((w, i) => (
              <li key={`${w.word}-${i}`} className={`vocab-item ${w.known ? 'known' : ''}`}>
                <div className="vocab-top-row">
                  <h3>{w.word}</h3>
                  <div className="vocab-actions">
                    <label>
                      <input
                        type="checkbox"
                        checked={w.known || false}
                        onChange={() => toggleKnown(i)}
                      />
                      <span className="checkbox-label">아는 단어</span>
                    </label>
                    <button className="delete-btn" onClick={() => removeWord(i)}>삭제</button>
                  </div>
                </div>
                <p>📖 뜻: {w.meaning}</p>
                {w.example && <p>✏️ 예문: {w.example}</p>}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
