import { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Chat.css';

type Message = {
  id: string;
  role: 'ai' | 'user';
  content: string;
};

export default function Chat() {
  const [isTopicSelected, setIsTopicSelected] = useState(false);
  const [selectedTopic, setSelectedTopic] = useState('');
  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState('');
  const navigate = useNavigate();

  const endRef = useRef<HTMLDivElement | null>(null);
  const scrollToBottom = () => endRef.current?.scrollIntoView({ behavior: 'smooth' });

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const addMessage = (role: 'ai' | 'user', content: string) => {
    setMessages((prev) => [
      ...prev,
      { id: `${Date.now()}-${Math.random()}`, role, content },
    ]);
  };

  const handleTopicSelect = (topic: string) => {
    setSelectedTopic(topic);
    setIsTopicSelected(true);
    // ÏïàÎÇ¥ Î©îÏãúÏßÄÎäî AIÍ∞Ä ÎßêÌïú Í±∏Î°ú Ï≤òÎ¶¨(ÏôºÏ™Ω)
    addMessage('ai', `Let's start the roleplay about "${topic}". You can type your first line!`);
  };

  const handleSend = () => {
    const text = input.trim();
    if (!text) return;

    // ÎÇ¥ Î©îÏãúÏßÄ(Ïò§Î•∏Ï™Ω)
    addMessage('user', text);
    setInput('');

    // (ÏòµÏÖò) ÏûÑÏãú AI ÏùëÎãµ Îç∞Î™® ‚Äî Ïã§Ï†ú API Ïó∞Îèô Ïãú Ïù¥ Î∂ÄÎ∂ÑÏùÑ ÎåÄÏ≤¥ÌïòÏÑ∏Ïöî.
    setTimeout(() => {
      addMessage('ai', `AI: "${text}" Ïóê ÎåÄÌïú ÏùëÎãµ ÏòàÏãúÏûÖÎãàÎã§.`);
    }, 400);
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleSend();
    }
  };

  const handleExit = () => {
    setIsTopicSelected(false);
    setSelectedTopic('');
    setMessages([]);
    setInput('');
    navigate('/feedback');
  };

  return (
    <div className="chat-container">
      <div className="chat-box">
        {isTopicSelected && (
          <button className="exit-button" onClick={handleExit}>
            ‚ùå
          </button>
        )}

        <div className="chat-header">
          {isTopicSelected ? `üí¨ Î°§ÌîåÎ†àÏù¥ Ï£ºÏ†ú: ${selectedTopic}` : 'üí¨ Î°§ÌîåÎ†àÏù¥ Ï£ºÏ†ú ÏÑ†ÌÉù'}
        </div>

        {!isTopicSelected && (
          <div className="topic-selection">
            <p>Ïñ¥Îñ§ Î°§ÌîåÎ†àÏù¥Î•º Ìï†ÍπåÏöî?</p>
            <button onClick={() => handleTopicSelect('Î≥ëÏõêÏóêÏÑú ÏùòÏÇ¨ÏôÄ ÌôòÏûê')}>üè• Î≥ëÏõê</button>
            <button onClick={() => handleTopicSelect('Î†àÏä§ÌÜ†ÎûëÏóêÏÑú Ï£ºÎ¨∏ÌïòÍ∏∞')}>üçΩÔ∏è Î†àÏä§ÌÜ†Îûë</button>
            <button onClick={() => handleTopicSelect('Í≥µÌï≠ÏóêÏÑú Ï≤¥ÌÅ¨Ïù∏ÌïòÍ∏∞')}>‚úàÔ∏è Í≥µÌï≠</button>
            <button onClick={() => handleTopicSelect('Ìò∏ÌÖîÏóêÏÑú Ï≤¥ÌÅ¨Ïù∏ÌïòÍ∏∞')}>üè® Ìò∏ÌÖî</button>
          </div>
        )}

        {isTopicSelected && (
          <>
            <div className="chat-messages">
              {messages.map((m) => (
                <div key={m.id} className={`message-row ${m.role}`}>
                  <div className={`message-bubble ${m.role}`}>
                    <span>{m.content}</span>
                  </div>
                </div>
              ))}
              <div ref={endRef} />
            </div>

            <div className="chat-input-area">
              <input
                className="chat-input"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={handleKeyDown}
                placeholder="Type your message..."
              />
              <button className="send-button" onClick={handleSend}>
                Send
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
}
