import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Voice.css';

export default function VoiceChat() {
    const [isTopicSelected, setIsTopicSelected] = useState(false);
    const [selectedTopic, setSelectedTopic] = useState('');
    const [isRecording, setIsRecording] = useState(false);
    const [messages, setMessages] = useState<string[]>([]);

    const navigate = useNavigate();

    const handleTopicSelect = (topic: string) => {
        setSelectedTopic(topic);
        setIsTopicSelected(true);
        setMessages([`You selected: ${topic}`]);
    };

    const handleExit = () => {
        navigate('/feedback');
    };

    const startRecording = () => {
        setIsRecording(true);
        console.log("Recording started...");
        // Ïã§Ï†ú ÏùåÏÑ± Ïù∏Ïãù Î°úÏßÅ Ï∂îÍ∞Ä
    };

    const stopRecording = () => {
        setIsRecording(false);
        console.log("Recording stopped...");
        setMessages([...messages, "AI's response based on recorded speech"]);
    };

    return (
        <div className="voice-chat-container">
            <div className="voice-chat-box">
                {isTopicSelected && (
                    <button className="exit-button" onClick={handleExit}>
                        ‚ùå
                    </button>
                )}

                <div className="voice-chat-header">
                    {isTopicSelected
                        ? `üé§ Î°§ÌîåÎ†àÏù¥ Ï£ºÏ†ú: ${selectedTopic}`
                        : 'üé§ Î°§ÌîåÎ†àÏù¥ Ï£ºÏ†ú ÏÑ†ÌÉù'}
                </div>

                {!isTopicSelected && (
                    <div className="topic-selection">
                        <p>Ïñ¥Îñ§ Î°§ÌîåÎ†àÏù¥Î•º Ìï†ÍπåÏöî?</p>
                        <button onClick={() => handleTopicSelect('Î≥ëÏõêÏóêÏÑú ÏùòÏÇ¨ÏôÄ ÌôòÏûê')}>üè• Î≥ëÏõê</button>
                        <button onClick={() => handleTopicSelect('Î†àÏä§ÌÜ†ÎûëÏóêÏÑú Ï£ºÎ¨∏ÌïòÍ∏∞')}>üçΩÔ∏è Î†àÏä§ÌÜ†Îûë</button>
                        <button onClick={() => handleTopicSelect('Í≥µÌï≠ÏóêÏÑú Ï≤¥ÌÅ¨Ïù∏ÌïòÍ∏∞')}>‚úàÔ∏è Í≥µÌï≠</button>
                        <button onClick={() => handleTopicSelect('Ìò∏ÌÖîÏóêÏÑú Ï≤¥ÌÅ¨Ïù∏ÌïòÍ∏∞')}>üè® Ìò∏ÌÖî</button>
                    </div>
                )}

                {isTopicSelected && (
                    <>
                        <div className="voice-chat-messages">
                            {messages.map((msg, idx) => (
                                <div key={idx} className="message">
                                    <span>{msg}</span>
                                </div>
                            ))}
                        </div>

                        <div className="voice-chat-controls">
                            <button
                                className={`record-btn ${isRecording ? 'active' : ''}`}
                                onClick={isRecording ? stopRecording : startRecording}
                            >
                                {isRecording ? 'Stop Recording' : 'Start Recording'}
                            </button>
                        </div>
                    </>
                )}
            </div>
        </div>
    );
}
