import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Chat.css';

export default function Chat() {
  const [isTopicSelected, setIsTopicSelected] = useState(false);
  const [selectedTopic, setSelectedTopic] = useState('');
  const [messages, setMessages] = useState<string[]>([]);
  const [input, setInput] = useState('');

  const navigate = useNavigate();

  const handleTopicSelect = (topic: string) => {
    setSelectedTopic(topic);
    setIsTopicSelected(true);
    setMessages([`You selected: ${topic}`]);
  };

  const handleSend = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, `üßë: ${input}`];
    setMessages(newMessages);
    setInput('');

    try {
      const response = await fetch('http://localhost:8080/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          topic: selectedTopic,
          userMessage: input,
        }),
      });

      const data = await response.json();
      setMessages((prev) => [...prev, `ü§ñ: ${data.reply}`]);
    } catch (err) {
      console.error('Error communicating with Gemini API:', err);
      setMessages((prev) => [...prev, '‚ùå Gemini ÏùëÎãµ Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.']);
    }
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleSend();
    }
  };

  const handleExit = () => {
    setIsTopicSelected(false);
    setSelectedTopic('');
    setMessages([]);
    setInput('');
    navigate('/feedback');
  };

  return (
      <div className="chat-container">
        <div className="chat-box">
          {isTopicSelected && (
              <button className="exit-button" onClick={handleExit}>
                ‚ùå
              </button>
          )}

          <div className="chat-header">
            {isTopicSelected ? `üí¨ Î°§ÌîåÎ†àÏù¥ Ï£ºÏ†ú: ${selectedTopic}` : 'üí¨ Î°§ÌîåÎ†àÏù¥ Ï£ºÏ†ú ÏÑ†ÌÉù'}
          </div>

          {!isTopicSelected && (
              <div className="topic-selection">
                <p>Ïñ¥Îñ§ Î°§ÌîåÎ†àÏù¥Î•º Ìï†ÍπåÏöî?</p>
                <button onClick={() => handleTopicSelect('Î≥ëÏõêÏóêÏÑú ÏùòÏÇ¨ÏôÄ ÌôòÏûê')}>üè• Î≥ëÏõê</button>
                <button onClick={() => handleTopicSelect('Î†àÏä§ÌÜ†ÎûëÏóêÏÑú Ï£ºÎ¨∏ÌïòÍ∏∞')}>üçΩÔ∏è Î†àÏä§ÌÜ†Îûë</button>
                <button onClick={() => handleTopicSelect('Í≥µÌï≠ÏóêÏÑú Ï≤¥ÌÅ¨Ïù∏ÌïòÍ∏∞')}>‚úàÔ∏è Í≥µÌï≠</button>
                <button onClick={() => handleTopicSelect('Ìò∏ÌÖîÏóêÏÑú Ï≤¥ÌÅ¨Ïù∏ÌïòÍ∏∞')}>üè® Ìò∏ÌÖî</button>
              </div>
          )}

          {isTopicSelected && (
              <>
                <div className="chat-messages">
                  {messages.map((msg, idx) => (
                      <div key={idx} className="message">
                        <span>{msg}</span>
                      </div>
                  ))}
                </div>

                <div className="chat-input-area">
                  <input
                      className="chat-input"
                      value={input}
                      onChange={(e) => setInput(e.target.value)}
                      onKeyDown={handleKeyDown}
                      placeholder="Type your message..."
                  />
                  <button className="send-button" onClick={handleSend}>
                    Send
                  </button>
                </div>
              </>
          )}
        </div>
      </div>
  );
}
